fn part2() void {
    var floor: isize = 0;
    for (0..input.len) |i| {
        if (input[i] == '('){
            floor += 1;
        } else{
            floor -= 1;
        }
        if (floor == -1) {
            std.debug.print("\nStep: {}\n", .{ i });
            return;
        }
    }
}


